mapper.select:

  	SELECT

		FROM
	hl_old_recycle
  	where 1=1 


		and order_Status = #{status}


		and trade_type = #{tradeType}


		and product_type = #{productType}


		and mobile = #{phoneNum}



		and c_time >= #{startDate}


		and c_time <= #{endDate}


      order by id desc

SELECT
count(1)
