mapper.select:
select

from hs_appointment
where id = #{id,jdbcType=INTEGER} and substation_id=#{substationId,jdbcType=INTEGER}

        select ent.appointment_mobile,ent.type,ent.address,ent.status,ent.pay_type as payType,UNIX_TIMESTAMP(ent.ctime)*100 as ctime,ifnull(ent.buy_time,'0') buyTime,
        (ent.type1+ent.type2+ent.type3+ent.type4+ent.type5+ent.type6+ent.type7+ent.type8+ent.type9) as countVal
        ,ifnull( (select nickname from hl_house_admin min where min.id = ent.house_admin_id),'null') nickname,
        ifnull( (select nickname from hl_house_bengbu house where house.id = ent.house_id),'null') housename
        from hs_appointment ent
            where  ent.substation_id = ${subId}

            and  ent.status = ${status}


            and ent.pay_type=#{payType}


            and  ent.appointment_mobile like CONCAT(CONCAT('%', #{mobile}), '%')


	        and ent.ctime >= #{starttime}


		    and ent.ctime <= #{endtime}
