mapper.select:
SELECT t.mchid, t.ctime, t.nickname, p.`name` AS province, c.`name` AS city, cn.`name` AS county, t.pot_x AS potx, t.pot_y AS poty, t.address FROM hs_machine_lianyun${substationNameSpell} t LEFT JOIN map_province p ON t.province_id = t.id LEFT JOIN map_city c ON t.city_id = c.id LEFT JOIN map_county cn ON t.county_id = cn.id where t.mchid in
#{item}
ORDER BY t.ctime ASC

SELECT t.mchid, l.nickname, l.pot_x AS potx, l.pot_y AS poty, t.type, t.weight, l.use_number AS useNumber, t.ctime AS flushTime FROM hs_machine_lianyun_track t LEFT JOIN hs_machine_lianyun${substationNameSpell} l ON t.mchid = l.mchid WHERE l.mchid is not null and t.substation_id=${substationId} and t.mchid in
#{item}
ORDER BY t.ctime ASC

select
	count(1)
from hs_machine_lianyun${substationNameSpell}
	where 1=1

	and mchid = #{mchid}


	and nickname = #{nickname}


	and id != #{id}
