mapper.select:
select sum(weight) as weight
	from hs_machine_lianyun_track where user_id=#{userId}
	  and  substation_id = ${substationId} and  ctime > #{startTime} and ctime < #{endTime}

select id,type,weight,cash_point,(select nickname from hs_machine_lianyun${spell} gbu  where gbu.mchid = ack.mchid
        ) as machineName,UNIX_TIMESTAMP(ctime)*100 as ctime from hs_machine_lianyun_track ack where card_id=#{cardId} and substation_id=#{substationId}
        order by id desc

mapper.update:
update hs_machine_lianyun_track
	set substation_id = ${substationId}
 		where mchid = #{mchId}

mapper.select:
SELECT hm.id, hm.mchid, hu.mobile, hm.card_id, hm.type, hm.weight, UNIX_TIMESTAMP(hm.ctime)*100 as ctime, hm.cash_point, IFNULL(NULL,hb.address) address, IFNULL(NULL,hb.area) area FROM hs_machine_lianyun_track hm LEFT JOIN hs_machine_lianyun${spell} hb ON hb.mchid = hm.mchid left JOIN hl_user hu ON hm.user_id = hu.id where hm.substation_id=${substationId}
and hu.mobile like CONCAT(CONCAT('%', #{mobile}), '%')
and hm.card_id like CONCAT(CONCAT('%', #{cardid}), '%')
and hm.ctime >= #{starttime}
and hm.ctime <= #{endtime}
and hm.type in (#{type})
